class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        a.insert(a.end(), a.begin(), a.begin() + k);
        int n = a.size();
        int cnt = 0;
        unordered_map<string, int> mp;
        vector<int> b(n + 1, 0);
        for (int i = 1; i < n; i++) {
            b[i] += b[i - 1] + abs(a[i] - a[i - 1]);
        }
        k--;
        for (int i = k + 1; i < n; i++) {
            if (b[i] - b[i - k] == k)
                cnt++;
        }
        return cnt;
    }
};
