class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> diff(n * 2);
        for (int i = 1; i < n; i++) {
            diff[i] = (colors[i - 1] ^ colors[i]);
        }
        diff[0] = colors[n - 1] ^ colors[0];
        for (int i = n; i < n * 2; i++) {
            diff[i] = diff[i - n];
        }
        int res = 0;
        k--;
        int cur = 0;
        for (int i = 0; i < k; i++) {
            cur += diff[i];
        }
        for (int i = 0; i < n; i++) {
            res += (cur == k);
            cur -= diff[i];
            cur += diff[i + k];
        }
        return res;
    }
};
