class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        int ans=0;
        vector<int>dp(n);
        dp[0]=1;
        for(int i=1;i<n;i++)
        {
            if(c[i]==c[i-1])
                dp[i]=1;
            else
                dp[i]=dp[i-1]+1;
        }
        vector<int>v(n);
        v[n-1]=n-1;
        for(int i=n-2;i>=0;i--)
        {
            if(dp[i]+1==dp[i+1])
            {
                v[i]=v[i+1];
            }
            else
            {
                v[i]=i;
            }
        }
        // for(auto i:v)
        // {
        //     cout<<i<<" ";
        // }
        // cout<<endl;
        int i=0;
        while(i<n)
        {
            while(i<n && v[i]==n-1 && v[i]-i+1<k && c[0]!=c[n-1] && v[i]-i+1+v[0]+1>=k)
            {
                ans++;
                i++;
            }
            if(i<n && v[i]-i+1>=k)
            {
                ans++;
            }
            i++;
        }

        return ans;
    }
};
