class Solution {
    fun numberOfAlternatingGroups(colors: IntArray, k: Int): Int {
        val n = colors.size
        var count = 0
        fun getIndex(i: Int): Int {
            return if (i < 0) n+i
            else if (i >= n) i-n
            else i
        }
        var yes = 0
        var no = 0
        for (offset in 0 .. k-2) {
            if (colors[getIndex(0+offset)] != colors[getIndex(0+offset+1)]) {
                yes ++
            }
        }
        if (yes == k-1) count++
        //println("i = 0 yes = $yes")
        for (i in 1 .. n-1) {
            if (colors[getIndex(i-1)] != colors[getIndex(i)]) {
                yes --
            }
            if (colors[getIndex(i+k-1)] != colors[getIndex(i+k-2)]) {
                yes ++
            }
            if (yes == k-1) count++
           // println("i = $i yes = $yes")
        }
        return count
    }
}
