class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), prev = -1;
        int res = 0, j = 0;

        for(int i=0;i<n+k-1; i++){

            if(colors[i%n]==prev){
                j = i;
            }
            if(i-j+1>=k){
                res++;
            }
            prev = colors[i%n];
        }
        return res;

    }
};
