class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int s = 1;
        unordered_map<int, int>mp;
        for(int i = 1; i < 2 * n; i++){
            if(colors[i % n] != colors[(i - 1 + n) % n])s++;
            else s = 1;
            if(s >= k && !mp.count(i % n)){
                mp[i % n]++;
                ans++;
            }
        }
        //if(n == k)return ans > 0 ? 1 : 0;
        return ans;
    }
};
