class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) 
    {
        int curr =0;
        int count = 0;
        int n = colors.length;
        for(int i=0;i<n;i++)
        {
            curr = Math.max(curr, i);
            while(curr-i<k-1)
            {
                if(colors[curr%n]==colors[(curr+1)%n]) break;
                curr++;
            }

            if(curr-i==k-1) count++;
        }

        return count;
    }
}
