class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int ans=0;

        vector<int> temp;
        for(auto& it: colors)
        temp.push_back(it);

        for(int i=0; i<=k-2; i++)
        temp.push_back(colors[i]);

        int count=1, t=1;
        while(t<n+k-1){
            if(temp[t]!=temp[t-1])
                count++;
            else
                count=1;
            if(count>=k)
                ans=ans+1;
            t++;
        }
        return ans;
    }
};
