using ll = long long;
using P = pair<int,int>;
using VI = vector<int>;
using VVI = vector<VI>;
using VL = vector<ll>;
using VVL = vector<VL>;



class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
      int n = a.size();
      rep(i, k - 1) a.emplace_back(a[i]);
      VI b(n + k - 1);
      rep(i, n + k - 2) b[i+1] = b[i] + (a[i] == a[i+1]);
      int ans = 0;
      rep(i, n) ans += b[i + k - 1] == b[i];
      return ans;
    }
};
