class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors = colors + colors
        l,pre,ans = 0,colors[0],0
        for i in range(n + k - 1):
            if colors[i] == pre:
                l = i
            if i - l + 1 >= k:
                ans += 1
            pre = colors[i]
        return ans
