class Solution {
    public int numberOfAlternatingGroups(int[] arr, int k) {
        int[] dp = new int[arr.length];
        int c=1;
        for (int i = 1; i < k; i++) {
            if(arr[i]!=arr[i-1]) c++;
            else break;
        }
        dp[0]=c;
        int n = arr.length;
        for (int i = n-1; i > 0; i--) {
            if(arr[i]!=arr[(i+1)%n]) dp[i]=dp[(i+1)%n]+1;
            else dp[i]=1;
        }
        c = 0;
        for (int i = 0; i < n; i++) {
            if(dp[i] >= k) c++;
        }
        return c;
    }
}
