class Solution {
public:
    int getans(int n, int k){
        return max(0, n-k+1);
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>arr=colors;
        for(int i=0;i<k-1;i++){
            arr.push_back(colors[i]);
        }
        int n=arr.size();
        int last=0;
        int ans=0;
        for(int i=1;i<n;i++){
            if(arr[i-1]==arr[i]){
                ans+=getans(i-1-last+1, k);
                last=i;
            }
        }
        ans+=getans(n-1-last+1, k);
        return ans;
    }
};
