class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        vector<int>arr;

        for(int j : colors){
            arr.push_back(j);
        }

        for(int j : colors){
            arr.push_back(j);
        }

        arr.pop_back();

        int n = colors.size();

        vector<int>dp(n);
        int len = 1;
        for(int i=n;i<(2*n)-1;i++){
            if(arr[i] != arr[i-1]){
                len++;
            }else{
                break;
            }
        }

        dp[n-1] = len;

        for(int i=n-2;i>=0;i--){
            if(colors[i] == colors[i+1]){
                dp[i] = 1;
            }else{
                dp[i] = dp[i+1]+1;
            }
        }

        int ans =0 ;
        for(int h : dp){
            if(h >= k){
                ans++;
            }
        }

        return ans;

    }
};
