class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>arr;
        int n=colors.size();
        for(int i=0;i<n+k-1;i++){
            arr.push_back(colors[(i)%n]);
        }
        int cnt=1,ans=0;
        for(int i=1;i<n+k-1;i++){
            if(colors[i%n]!=colors[(i-1)%n]){
                cnt++;
            }
            else {
                cnt=1;
            }
            if(cnt>=k)ans++;
        }
        return ans;
    }
};
