int A[200000], n;
int dp[200000];

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        n = colors.size();
        for(int i = 0;i<n;++i) A[i] = colors[i];
        for(int i=0;i<k;++i) A[i+n] = A[i];

        dp[n+k-1] = n+k-1;
        for(int i=n+k-2;i>=0;--i) {
            if(A[i]!=A[i+1]) dp[i] = dp[i+1];
            else dp[i] = i;
        }


        int ans = 0;
        for(int i=0;i<n;++i) {
            int len = dp[i] - i + 1;
            if(len >= k) ans++;
        }
        return ans;
    }
};
