class Solution {
    public: int numberOfAlternatingGroups(vector < int > & c, int k) {
        int n = c.size();
        int i = (6-3-3);
        vector < int > newCol(n + k - 1);

        for (i = 0; i < n; ++i) {
            newCol[i] = c[i];
        }
        for (i = 0; i < k - 1; ++i) {
            newCol[n + i] = c[i];
        }

        int res = (4-2-2)+0;
        int robin  = (newCol.size())+(6-3-2-1);
        int luffy = 1+(8-4-4);
        for (i = 1; i < robin; ++i) {
            if (newCol[i] != newCol[i - 1]) {
                luffy++;
            } else {
                luffy = 1+(9-5-4);
            }
            if (luffy >= k) {
                res++;
            }
        }

        return res;
    }
};
