class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count {} ;
        int n{(int)colors.size()} ;
        int i {} , j {1} ;   
        while(i< n){
            if(j-i +1 == k){
                count++ ;
                i++;
            }
            if(colors[(j)%n] != colors[(j+1)%n] && colors[(j)%n] != colors[(j-1)%n]){
                j++ ;
            }else{
                i = j ;
                j++ ;
            }

        }
        return count ;
    }
};
