class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        int cur = 0;
        for(int i = 0; i < k - 1; i++) 
            cur += (a[i] != a[(i + 1) % n]);
        int ans = 0;
        int i = 0, j = k - 1;
        while(1) {
            if(cur == k - 1) ans += 1;
            i++, j++;
            if(i == n) break;
            if(a[i] != a[i - 1]) cur -= 1;
            if(a[j % n] != a[(j - 1 + n) % n]) cur += 1;
        }
        return ans;
    }
};
