#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0;i<k;i++)
        {
            colors.push_back(colors[i]);
        }
        n+=k;
        vector<int>arr(n+1,0);
        int an=0;
        k--;

        for(int i=0;i<n;i++)
        {
            if(i==0)
                continue;
            arr[i]+=arr[i-1]+abs(colors[i]-colors[i-1]);
        }

        int i=k+1;
        while(i<n){
            if(i-k>=0 && arr[i]-arr[i-k]==k)
                an++;
            i++;
        }
        return an;
    }
};
