class Solution {
public:
    vector<int> diff;
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        return x(colors, k);
    }

    int x(vector<int>& colors, int k) {
        int n = colors.size();
        if (k == 1) return n;  

        diff.resize(2*n);

        for (int i = 0; i < 2*n; i++) {
            diff[i] = colors[i % n] ^ colors[(i + 1) % n];  
        }

        int count = 0;
        int windowSum = 0;

        for (int i = 0; i < k - 1; i++) {
            windowSum += diff[i];
        }

        for (int i = 0; i < n; i++) {
            if (windowSum == k - 1) {
                count++;
            }
            windowSum = windowSum - diff[i] + diff[i + k - 1];
        }

        return count;
    }
};
