class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int res = 0;
        int n = arr.size();
        for (int i = 0; i < n; i++) {
            arr.push_back(arr[i]);
        }
        int count = 1;
        for (int i = 1; i < n + k - 1; i++) {
            if (arr[i] != arr[i - 1]) {
                count++;
            } else {
                if (count >= k) {
                    res += (count - k + 1);
                }
                count = 1;
            }
        }
        if (count >= k) {
            res += (count - k + 1);
        }
        return res;
    }
};
