class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int l = 0, r = 1, ans = 0, n = colors.size();
        while (l < n and r - k < n) {
            int m = r, present = m % n;
            while (m < n + k - 1 and colors[present] != colors[(present - 1 + n) % n]) {
                m += 1;
                present = m % n;
            }
            ans += max(m - l - k + 1, 0);
            l = m;
            r = l + 1;
        }
        return ans;
    }
};
