import java.util.*;

class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        List<Integer> extendedColors = Arrays.stream(colors).boxed().collect(Collectors.toList());

        for (int i = 0; i < k - 1; i++) {
            extendedColors.add(colors[i]);
        }

        int ans = 0;
        int cnt = 1;

        for (int i = 1; i < extendedColors.size(); i++) {
            if (!Objects.equals(extendedColors.get(i), extendedColors.get(i - 1))) {
                cnt++;
            } else {
                cnt = 1;
            }

            if (cnt >= k) {
                ans++;
            }
        }

        return ans;
    }
}
