class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=(int)colors.size();
        vector<int> temp(n+k-1);
        for(int i=0;i<n+k-1;i++){
            temp[i]=colors[i%n];
        }
        int cur=1;
        int ans=0;
        for(int i=1;i<n+k-1;i++){
            if(temp[i]==temp[i-1]){
                cur=1;
                continue;
            }
            else{
                cur++;
                if(cur>=k){
                    ans++;
                }
            }
        }
        return ans;
    }
};
