class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(),colors.begin(),colors.begin()+k-1);
        int n = colors.size();
        int ans= 0;
        int count = 1;

        for(int i=1;i<n;i++)
        {
            if(colors[i] == 1-colors[i-1])
            {
                count++;
                if(count==k)
                {
                    ans++;
                    i++;
                    while(i<n && colors[i-1] == 1-colors[i])
                    {
                        ans++;
                        i++;
                    }
                    count = 1;
                }
            }
            else 
            {
                count=1;
            }
        }
        return ans;

    }
};
