class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int count=0;
        int n=colors.length;
        int i=0;
        while(i<n){
            if(check(colors,i,k)){
                    if(k%2==1){
                        while(i<n&&colors[i]==colors[(i+k-1)%n]){
                            i++;
                            count++;
                        }
                    }
                    else{
                        while(i<n&&colors[i]!=colors[(i+k-1)%n]){
                            i++;
                            count++;
                        }
                    }
            }
            i++;
        }
        return count;
    }
    private boolean check(int[] colors,int i,int k){
        int n=colors.length;
        int prev=i;
        for(int j=i+1;j<i+k;j++){
            int x=j%n;
            if(colors[x]==colors[prev])    return false;
            prev=x;
        }
        return true;
    }
}
