class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> X(n * 2), Y(n * 2);

        for (int i = 0; i < n * 2; i++)
            X[i] = colors[i % n];

        Y[0] = 1;

        for (int i = 1; i < n * 2; i++)
        {
            if (X[i] != X[i - 1])
                Y[i] = Y[i - 1] + 1;
            else
                Y[i] = 1;
        }

        int ans = 0;

        for (int i = n; i < n * 2; i++)
            if (Y[i] >= k)
                ans++;

        return ans;
    }
};
