class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for (int i=0;i<n;i++)
            colors.push_back(colors[i]);
        vector<int> dis(2*n-1,0);
        for (int i=0;i<2*n-1;i++)
            dis[i]=abs(colors[i+1]-colors[i]);
        vector<int> sumv(2*n,0);
        for (int i=1;i<2*n;i++)
            sumv[i]=sumv[i-1]+dis[i-1];
        int ans=0;
        for (int i=k-2;i<n+k-2;i++)
            if (sumv[i+1]-sumv[i+2-k]==k-1)
                ans++;
        return ans;
    }
};
