class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        colors.insert(colors.end(),colors.begin(),colors.end());
        int i = 0;
        int j = 1;
        int ct = 0;
        while(j<=n+k-2){
            if(colors[j]==colors[j-1]){
                i = j;
                j++;
                continue;
            }
            if(j-i+1<k) j++;
            else if(j-i+1==k){
                ct++;
                i++;
            }
        }
        return ct;
    }
};
