class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int ans = 0;
        int left = 0;

        vector<int> temp;

        for(int x:c)temp.push_back(x);
        for(int x:c)temp.push_back(x);
        int i = 1;
        while(left<c.size() && i<temp.size()){

            if(temp[i]==temp[i-1]){
                left=i;
            }            
            while(i-left+1>k)left++;
            if(left>=c.size())break;
            if(i-left+1==k)ans++;
            //cout<<left<<" "<<ans<<endl;
            i++;
        }
        return ans;
    }
};
