class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ans=0;

        int n=v.size();
        for(int i=0;i<k-1;i++){
            v.push_back(v[i]);
        }
        k--;
        int l=0,r=0;
        while(l<n){
            if(r-l<k){
                if(r+1<v.size() && v[r]!=v[r+1]){
                    r++;
                }
                else{
                    l=r+1;
                    r=r+1;
                }
            }
            else if(r-l==k){
                ans++;
                if(r+1<v.size() && v[r]!=v[r+1]){
                    l++;
                    r++;
                }
                else{
                    l=r+1;
                    r=r+1;
                }
            }
        }

        return ans;
    }
};
