class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        bool check = true;
        int n = colors.size(), count = 0, prev, neww;
        for (int j = 0; j < k - 1; j++) {
            if (colors[j % n] == colors[(j + 1) % n]) {
                check = false;
                break;
            }
        }
        if (check) count++;
        for (int i = 1; i < n; i++) {
            prev = (i - 1) % n;
            neww = (i + k - 1) % n;
            if (colors[prev] != colors[(prev + 1) % n] && colors[(i + k - 2) % n] != colors[neww] && check) {
                count++;
            } else {
                check = true;
                for (int j = 0; j < k - 1; j++) {
                    if (colors[(i + j) % n] == colors[(i + j + 1) % n]) {
                        check = false;
                        break;
                    }
                }
                if (check) count++;
            }
        }
        return count;
    }
};
