class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        diff = [abs(b - a) for a, b in pairwise(colors)]
        diff.append(abs(colors[-1] - colors[0]))
        k -= 1
        diff.extend(diff[:k - 1])
        ans = 0
        for key, it in groupby(diff):
            if key:
                count = sum(1 for _ in it)
                if count >= k:
                    ans += count - k + 1
        return ans
