class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int nn = a.size();
        for(int i=0; i<k-1; i++){
            a.push_back(a[i]);
        }

        int n = a.size();
        vector<int> na;

        for(int i=0; i<n-1; i++){
            if(a[i] == a[i+1]){
                na.push_back(i);
            }
        }

        int ans = 0;
        for(int i=0; i<nn; i++){
            int L = i;
            int R = i+k-1;

            //cout << L << " " << R << " : ";

            auto it = lower_bound(na.begin(), na.end(), L);
            int lb = R;
            if(it != na.end()){
                lb = *it;
                if(lb >= R){
                    ans++;
                }
                //cout << lb <<'\n';
            }

            else{
                ans++;
                //cout << -1 << '\n';
            }
        }
        return ans;
    }
};
