class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {

        int prv=-1,len=0,ans=0,n=a.size();

        for(int i=n-k+1;i<n;i++){
            if(a[i]==prv){
                len=1;
            }
            else len++;
            prv=a[i];
        }

        for(int i=0;i<n;i++){
            if(a[i]!=prv)len++;
            else len=1;
            prv=a[i];
            len=min(len,k);
            // cout<<len<<" ";
            ans+=(len==k);
        }
        // cout<<endl;
        return ans;
    }
};
