class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for (int i = 0; i < k-1; i++) {
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        vector<int> len(n);
        len[0] = 1;
        int ans = 0;
        for (int i = 1; i < n; i++) {
            if (colors[i] != colors[i-1]) {
                len[i] = len[i-1] + 1;
            } else {
                len[i] = 1;
            }
            ans += len[i] >= k;
        }
        return ans;
    }
};
