class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)

        count = 0

        if k == 1:
            return n

        alt = 1
        i = 1
        while i < n + k - 1:
            current = i
            previous = i - 1

            if current >= n:
                current -= n
            if previous >= n:
                previous -= n

            if colors[current] != colors[previous]:
                alt += 1
            else:
                alt = 1

            if alt >= k:
                count += 1

            i += 1

        return count
