class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int sz = 1,ans = 0,start = 0;
        for(int end = 1;start<n;end++) {
            if(sz < k) {
                if(colors[(end-1+n)%n] != colors[end%n]) {
                    sz++;
                }
                else {
                    start = end;
                    sz = 1;
                }
            }
            if(sz == k) {
                ans++;
                start++;
                sz--;
            }
        }
        return ans;
    }
};
