class Solution {
public:
    bool check(vector<int>& arr, int te){
        if(arr[te] != arr[(te - 1 + arr.size()) % arr.size()]) return true;
        return false;
    }
    int solve(vector<int>& arr, int k){
        int i = 0, j = 1, ans = 0, n = arr.size();
        while (j < arr.size() + k && i < arr.size()) {
            int tj = j, te = tj % n;
            while (tj < n + k - 1 && check(arr,te)) {
                tj++;
                te = tj % n;
            }

            ans += max(tj - i - k + 1, 0);
            i = tj;
            j = i + 1;
        }
        return ans;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        return solve(colors, k);
    }
};
