class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();

        if(n<k) return 0;

        vector<int> xtracolor=colors;

        for(int i=0;i<k-1;++i){
            xtracolor.push_back(colors[i]);
        }

        int trip=0;


        bool altgrp=true;
        for(int i=0;i<k-1;++i){
            if(xtracolor[i]==xtracolor[i+1]){
                altgrp=false;
                break;
            }
        }
        if(altgrp) trip++;

        for(int i=1;i<n;++i){
            if(xtracolor[i-1]==xtracolor[i]|| xtracolor[i+k-2]==xtracolor[i+k-1]){
                altgrp=false;
            }
            else if(altgrp && xtracolor[i-1]!=xtracolor[i+k-2]){
                altgrp=true;
            }

            if(altgrp){
                trip++;
            }
            else{
                altgrp=true;
                for(int j=0;j<k-1;j++){
                    if(xtracolor[i+j]==xtracolor[i+j+1]){
                        altgrp=false;
                        break;
                    }
                }
                if(altgrp) trip++;
            }
        }

        return trip;
    }
};
