class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        long long int left = 0,right =1;
        long long int ans = 0;
        long long cnt = k;
        int n =colors.size();

        while(right < n+cnt && left < n){
            long long int x = right;
            long long int y = x%n ;

            while(colors[y] != colors[(y-1+n)%n] && x < n+k-1){
                x++;
                y = x%n;
            }
            if(x-left-cnt+1>0) ans += (x-left-cnt+1);

            // ans += max(x - left - cnt,ll(0));
            left = x;
            right = x+1; 
        }

        return ans;
    }
};
