class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        l = 1
        c = colors[k - 1]
        for i in range(k - 2, -1, -1):
            if colors[i] != colors[i + 1]:
                l += 1
            else:
                break
        ans = 0
        for i in range(k, k + n):
            if colors[i % n] != colors[(n + i - 1) % n]:
                l = min(l + 1, k)
            else:
                l = 1
            if l == k:
                ans += 1
        return ans
