class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v , int k) {
        int n = v.size();
        for(int i = 0 ; i < k - 1 ; i++) v.push_back(v[i]);
        vector<int> hsh(v.size() , 0);
        for(int i = 1 ; i < v.size() ; i++) if(v[i] == v[i - 1]) hsh[i - 1] = 1;
        for(int i = 1 ; i < v.size() ; i++) hsh[i] += hsh[i - 1];
        int ans = 0;
        for(int i = 0 ; i < n ; i++){
            int sum = hsh[i + k - 1];
            if(i) sum -= hsh[i - 1];
            if(i != n - 1 && v[i + k - 1] == v[i + k]) sum--;
            if(!sum) ans++;
        }
        return ans;
    }
};
