class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> t = colors;
        for(int i : colors) t.push_back(i);
        int n = colors.size(), res = 0;
        for(int i = 0, j = 0, cur = 0; i < 2*n; ++i) {
            if(i && t[i] != t[i-1]) cur++;
            if(i-j+1 > k) {
                if(t[j+1] != t[j]) cur--;
                j++;
            }
            if(i >= n && cur == k-1) {
                res++;
            }
        }
        return res;
    }
};
