class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int sz=colors.size();
        vector<int> same(sz,0);
        for(int i=0;i<sz;i++){
            if(i==sz-1){
                same[i]=same[i-1];
            }
            else if(i==0){
                if(colors[i]==colors[i+1]){
                    same[i]=1;
                }
            }
            else if(colors[i]==colors[i+1]){
                same[i]=same[i-1]+1;
            }
            else{
                same[i]=same[i-1];
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            int tmp=(i>0 ? same[i-1] : 0);
            if(same[i+k-2]-tmp==0){
                // cout<<i<<" ";
                ans++;
            }
        }
        return ans;
    }
};
