class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int ans = 0;
        int n = c.size();
        for (int i = 0; i < n; ++i){
            c.push_back(c[i]);
        }
        vector<int>s(2*n+1);
        int cnt=0;
        s[0] = (c[0]!=c[1]);
        s[k-1] = (c[k-1]!=c[k-2]);

        for (int i = 1; i <=k-2 ; ++i){
            s[i] = (c[i]!=c[i+1] && c[i]!=c[i-1]);
        }
        cnt = accumulate(s.begin(),s.end(),0);
        ans += (cnt==k);
        deb(c,k);
        deb(s,k,cnt);
        for (int i = k; i <n-1+k ; ++i){
            cnt-=s[i-k];
            cnt-=s[i-1];
            cnt-=s[i-k+1];

            s[i-1] = (c[i-1]!=c[i-2] && c[i-1]!=c[i]);
            s[i] = (c[i]!=c[i-1]);
            s[i-k+1] = (c[i-k+1]!=c[i-k+2]);

            cnt+=s[i];
            cnt+=s[i-1];
            cnt+=s[i-k+1];
            ans+=(cnt==k);
        }
        return ans;
    }
};

