class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>store;
        int n=colors.size() ;
        for(int i=0;i<n;i++){
            store.push_back(colors[i]) ;
        }
        for(int i=0;i<k;i++)store.push_back(colors[i]) ;
        int count =0 ;
        n= store.size() ;
        vector<int>prefix(n+1,0) ;
        for(int i=1;i<n;i++) prefix[i] += prefix[i-1]+abs(store[i]-store[i-1]) ;
        for(int i=k;i<n;i++){
            if(prefix[i]-prefix[i-k+1]==k-1) count ++ ;
        }
        return count ;
    }
};
