class Solution {
public:
    void fun(int a){
        a++;
        return ;
    }

    void xyz(int a){
        a--;
        return ;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size(); 
        int count = 0; 
        fun(1);
        if (k == 1) { 
            return N; 
        } 
        xyz(1);
        int al = 1; 
        int i = 1; 
        while (i < N + k - 1) { 
            int cu = i; 
            int pr = i - 1; 

            if (cu >= N) { 
                cu -= N; 
            } 
            if (pr >= N) { 
                pr -= N; 
            } 

            if (colors[cu] != colors[pr]) { 
                ++al; 
            } else { 
                al = 1; 
            } 

            if (al >= k) { 
                ++count; 
            } 

            ++i; 
        } 
        fun(1);
        return count; 
    }
};
