function numberOfAlternatingGroups(colors: number[], k: number): number {
  colors = colors.concat(colors.slice(0, k - 1))
  const n = colors.length
  let res = 0
  for (let i = 0; i < n; i++) {
    let j = i + 1
    for (; j < n; j++) {
      if (colors[j] === colors[j - 1]) break
    }
    res += Math.max(0, j - i - (k - 1))
    i = j - 1
  }
  return res
}
