class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        # Mine in a contest
        tot, cnt, prev, l = 0, 0, None, len(colors)
        for c in colors + colors[:k - 1]:
            if c != prev:
                cnt += 1
                if cnt >= k:
                    tot += 1
            else:
                cnt = 1
                if l <= 0:
                    break
            prev = c
            l -= 1

        return tot
